body.script:
let cards = [];
let currentIndex = 0;
let isFlipped = false;

// Load cards from the generated HTML
function loadCards() {
    const cardElements = document.querySelectorAll('.card-data');
    cards = Array.from(cardElements).map(el => {
        const unitName = el.getAttribute('data-front');
        const profileName = el.getAttribute('data-profile');
        const charName = el.getAttribute('data-char');
        const value = el.getAttribute('data-back');

        // Build front text with filtering logic
        let frontText = unitName;

        // Add profile name if it exists and differs from unit name
        if (profileName && profileName !== unitName && profileName !== 'Leader') {
            frontText += ' | ' + profileName;
        }

        // Add characteristic name if it's not "Description"
        if (charName && charName !== 'Description') {
            frontText += ' | ' + charName;
        }

        return {
            front: frontText,
            back: value
        };
    }).filter(card => {
        // Filter out Leader profiles
        const cardEl = cardElements[cards.length];
        return cardEl && cardEl.getAttribute('data-profile') !== 'Leader';
    });

    if (cards.length > 0) {
        showCard(0);
    }
}

function showCard(index) {
    if (cards.length === 0) return;

    currentIndex = index;
    const card = cards[currentIndex];

    document.getElementById('front-content').textContent = card.front;
    document.getElementById('back-content').textContent = card.back;
    document.getElementById('counter').textContent = `${currentIndex + 1} / ${cards.length}`;

    // Reset flip state
    if (isFlipped) {
        flipCard();
    }
}

function flipCard() {
    const flashcard = document.getElementById('flashcard');
    flashcard.classList.toggle('flipped');
    isFlipped = !isFlipped;
}

function nextCard() {
    if (cards.length === 0) return;
    const nextIndex = (currentIndex + 1) % cards.length;
    showCard(nextIndex);
}

function previousCard() {
    if (cards.length === 0) return;
    const prevIndex = (currentIndex - 1 + cards.length) % cards.length;
    showCard(prevIndex);
}

function shuffleCards() {
    for (let i = cards.length - 1; i > 0; i--) {
        const j = Math.floor(Math.random() * (i + 1));
        [cards[i], cards[j]] = [cards[j], cards[i]];
    }
    showCard(0);
}

// Keyboard navigation
document.addEventListener('keydown', (e) => {
    if (e.key === 'ArrowRight' || e.key === ' ') {
        e.preventDefault();
        if (isFlipped) {
            nextCard();
        } else {
            flipCard();
        }
    } else if (e.key === 'ArrowLeft') {
        e.preventDefault();
        previousCard();
    } else if (e.key === 'ArrowUp' || e.key === 'ArrowDown') {
        e.preventDefault();
        flipCard();
    }
});

// Load cards when page loads
window.addEventListener('DOMContentLoaded', loadCards);
